<script setup>
import { ref, computed } from "vue";
import { checkLogin } from "../../utils/common";
import { createSwap, createAvatar } from "../../api/faceSwap";
import { getTmpDetail } from "../../api/swapTemplate.js";
import { imageMode } from "../../context.js";
import { onLoad, onShow } from "@dcloudio/uni-app";
import { closeIcon } from "../../common/svgBase64";
import { baseUrl, requireLogin } from "../../utils/request";
import { DraftStore, DraftType } from "../../store/draft";
const closeIconForImage = ref(closeIcon);

import { useStore } from "vuex";
import { useI18n } from "vue-i18n";
import {
  getDigitalAvatarList,
  removeDigitalAvatar,
} from "../../api/digitalAvatar";
import { getUserInfo, getVipCount } from "../../api/user";
import { AblumStore, AblumType } from "../../store/album";
const store = useStore();
const { t } = useI18n();

const userDraft = computed(() => DraftStore.getDraft(store));
const totalDrawNum = ref(1);
const balanceDraw = ref(0);
const renderWidth = 686;
const renderHeight = 714; //renderWidth * 1.5;
const showRechargeBtn = ref(false);

/**
 * @typedef {Object} DigitalAvatar 数字分身信息
 * @property {number} id - 数字分身ID
 * @property {number} face_id - 人脸ID
 * @property {string} face_image - 脸部图片地址
 * @property {string} user_image - 上传图片地址
 */

/** @type {import('vue').Ref<DigitalAvatar[]>} */
const digitalAvatarList = ref([]);

/** @type {import('vue').Ref<DigitalAvatar | null>} */
const selectedAvatar = ref(null);

const chooseImage = async (sourceType) => {
  const isLogin = await checkLogin({ delta: 1 });
  if (!isLogin) {
    return;
  }

  uni.chooseImage({
    count: 1,
    sizeType: ["original", "compressed"],
    sourceType: [sourceType],
    success: (res) => {
      console.log(JSON.stringify(res.tempFilePaths));
      confirmUpload(res.tempFilePaths[0]);
    },
    fail: () => {},
  });
};

const confirmUpload = async (filePath) => {
  console.log("filePath = ", filePath);
  if (!filePath) {
    return;
  }

  uni.showLoading({ title: "图片上传中", mask: true });
  try {
    /*const res = await createAvatar(filePath);
    uni.hideLoading();
    const data = res.data; //JSON.parse(res.data);
    if (data.code === -1) {
      requireLogin({
        toastMsg: t("api-toast.login-status-expired"),
      });
    }
    if (data.code !== 20000) {
      uni.showToast({
        title: data.msg || t("api-toast.server-error"),
        icon: "none",
      });
      return;
    }
    //getAvatarList();
    selectedAvatar.value = res.data.url;*/
    const token = uni.getStorageSync("token");

    // 如果存在 token，则将其添加到请求头中
    let header;
    if (token) {
      header = { Authorization: token };
    } else {
      requireLogin();
      return Promise.reject(new Error("No token found"));
    }

    uni.uploadFile({
      //url: `${baseUrl}/api/face_swap/create_avatar`,
      url: `${baseUrl}/system/upload`,
      filePath: filePath,
      name: "file",
      header: header,
      success: (res) => {
        uni.hideLoading();
        const data = JSON.parse(res.data);
        if (data.code === -1) {
          requireLogin({
            toastMsg: t("api-toast.login-status-expired"),
          });
        }
        if (data.code !== 20000) {
          uni.showToast({
            title: data.msg || t("api-toast.server-error"),
            icon: "none",
          });
          return;
        }
        //getAvatarList();
        selectedAvatar.value = data.data.url;
      },
      fail: () => {},
    });
  } catch (err) {
    console.log(err);
    uni.hideLoading();
  }
};

const onSelectAvatar = (avatar) => {
  console.log(avatar, "on select avatar");
  selectedAvatar.value = avatar;
};
const onDeleteAvatar = (avatar) => {
  uni.showModal({
    content: "是否删除该分身？",
    confirmText: "确认删除",
    confirmColor: "#FF0000",
    success: function (res) {
      if (res.confirm) {
        console.log("确认删除");
        digitalAvatarList.value = digitalAvatarList.value.filter(
          (item) => item.id !== avatar.id
        );
        removeDigitalAvatar({ id: avatar.id });
      } else if (res.cancel) {
        console.log("用户点击取消");
      }
    },
  });
};
const onResetSelectedAvatar = () => {
  selectedAvatar.value = null;
};

const isSelected = (avatar) => {
  return selectedAvatar.value && selectedAvatar.value.id === avatar.id;
};

const getAvatarList = async () => {
  const res = await getDigitalAvatarList();
  if (res.code === 1) {
    const faces = res.data.list || [];
    digitalAvatarList.value = faces;
    selectedAvatar.value = digitalAvatarList.value[0];
  }
};
const getTemplateDetail = async () => {
  const res = await getTmpDetail(userDraft.value.template.id);
  if (res.code === 20000) {
    //debugger;
  }
};
const designImage = async () => {
  //他们要搞云上传图片,这里直接模拟已经得到云图片地址
  // digitalAvatarList.value = [
  //   {
  //     id: selectedAvatar.value||"https://mydolldoll-test.oss-cn-shanghai.aliyuncs.com/7453ac0a-6bbc-46ae-8723-d2ba74a9128b",
  //     face_image: selectedAvatar.value||"https://mydolldoll-test.oss-cn-shanghai.aliyuncs.com/7453ac0a-6bbc-46ae-8723-d2ba74a9128b",
  //   },
  // ];
  // selectedAvatar.value =
  //   "https://mydolldoll-test.oss-cn-shanghai.aliyuncs.com/7453ac0a-6bbc-46ae-8723-d2ba74a9128b";
  if (!selectedAvatar.value) {
    uni.showToast({
      title: "请选择一张图片",
      icon: "none",
    });
    return;
  }

  onSubmit();
};

const getUserBalance = () => {
  getVipCount().then((res) => {
    balanceDraw.value = res.Data?.number || 0;
    store.commit("setBalance_draw", balanceDraw.value);
  });
};

onLoad(() => {
  getUserBalance();
  // getAvatarList();
  getTemplateDetail();
});

onShow(() => {
  uni.$once("navigating_back:from_page_login", (event) => {
    console.log("从登录页返回", event);
    getUserBalance();
    getAvatarList();
  });
});

// 获取主题
const getTheme = () => {
  return imageMode.value === "gallary" ? "gallaryTheme" : "portraitTheme";
};

// 当前预览的模板
const currTemplate = computed(() => {
  return userDraft.value.template;
});
console.log("currTemplate", currTemplate.value);

// 提交绘图任务
const onSubmit = async () => {
  if (!selectedAvatar.value) {
    uni.showToast({
      title: "请先选择一个数字分身",
      icon: "none",
    });
    return;
  }
  //debugger;
  let temp_uuid = userDraft.value.template.id;
  store.commit("setTempUuid", temp_uuid);
  const res = await createSwap({
    template_uuid: temp_uuid,
    // avatar_id: selectedAvatar.value.id,
    origin_image: selectedAvatar.value,
  });

  if (res.code !== 20000) {
    uni.showToast({
      title: res.msg || "服务器开小差了",
      icon: "none",
    });
    // uni.navigateTo({
    //   url: "/pages/draw/result?uuid=" + res.uuid,
    // });
    return;
  }
  DraftStore.setTemplate(
    store,
    new DraftType.Template({
      id: temp_uuid,
      name: userDraft.value.template.name,
      target_image: userDraft.value.template.target_image, //模板图-》用户上传图//selectedAvatar.value,
    })
  );
  getUserBalance();
  // AblumStore.setImageList(store, [
  //   new AblumType.ImageItem({
  //     result_image: res.data.result.result_image,
  //     template_name: userDraft.value.template.name,
  //   }),
  // ]);
  uni.navigateTo({
    url: "/pages/draw/result?uuid=" + res.uuid,
  });
};
</script>

